target:
GGL.begin
node Cube { size: 1; }
emit Cube;
GGL.end

GGL.begin
node Cube { size: 2; }
emit Cube;
GGL.end

GGL.begin
node Cube { size: 1; material: "red"; }
emit Cube;
GGL.end

GGL.begin
for i in [0,1,2] {
  node Cube { size: 1; translate: [i*2,0,0]; }
  emit Cube;
}
GGL.end

GGL.begin
node Square { size: 1; }
emit Square;
GGL.end

GGL.begin
node Triangle { size: 1; }
emit Triangle;
GGL.end

GGL.begin
node Cube { size: 1; rotate: [0,45,0]; }
emit Cube;
GGL.end

GGL.begin
node Cube { size: 1; scale: [0.5,0.5,0.5]; }
emit Cube;
GGL.end

GGL.begin
graph G {
  node A { type: "Cube"; translate: [-2,0,0]; }
  node B { type: "Cube"; translate: [2,0,0]; }
  edge A -> B { kind: "link"; }
  emit G;
}
GGL.end

GGL.begin
let entropy = @entropy;
if entropy > 0.7 {
  node Warning { level: "high"; }
  emit Warning;
} else {
  node Ok { level: "normal"; }
  emit Ok;
}
GGL.end
